
public class diamond {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=1;i<10;i+=2)
{
	for(int j=0;j<10-i/2;j++)
		System.out.print("");
	for(int j=0;j<i;j++)
		System.out.print("*");
	System.out.println("\n");
}
for(int i=7;i>0;i-=2){
	for(int j=0;j<10-i/2;j++)
		System.out.print("");
	for(int j=0;j<i;j++)
		System.out.print("*");
	System.out.println("\n");
	}
	}

}
